import logo from './logo.svg';
import './App.css';
import { useState } from "react"
//경로는 현재 파일을 기준으로 목적지를 찾아가면 된다.
//import {Farewell, Greeting} from './Greetings'
import Todo from './Todo';
import { List, Paper} from '@mui/material';


function App() {
  //우리는 하나의 할 일을 객체로 관리할 것이다.
  //id, title, done
  const [items, setItems] = useState([
  {
    id:"0",
    title:"Hello world 1",
    done: true
  },
  {
    id:"1",
    title:"Hello world 2",
    done: false
  }
])

//React는 key속성에 들어있는 값을 참고해서, 리스트의 요소가 변경될 경우 어떤 요소가 변경되었는지 빠르게 파악할 수 있다.
const todoItems = items.length > 0 && 
  <Paper style={{margin: 16}}>
    <List>
      {items.map((item) => (
        <Todo item={item} key={item.it} />
      ))}
    </List>
  </Paper>

  return (
    <div className="App">
      {todoItems}
    </div>
  );

}

export default App;
