//현재 파일에서는 checkBox와 lable 컴포넌트를 만들어보자
import { useState } from "react";
import { ListItem, ListItemText, InputBase, checkBox, Checkbox} from '@mui/material'

let Todo = (props) =>{
    const [item, setItem] = useState(props.item);
    return(

        //html코드가 들어가는 부분
        //속성을 쓸 때 카멜케이스로 작성하기
        //onclick -> onClick
        //class -> className
        <ListItem>
            <Checkbox checked={item.done} />
            <ListItemText>
                <InputBase
                    inputProps={{"aria-label" : "naked"}}
                    type="text"
                    id={item.id}
                    name={item.id}
                    value={item.title}
                    multiline={true}
                    fullWidth={true}
                />
            </ListItemText>
            {/* label태그는 for속성에 name값으로 연결해서 어떤 요소와 연결될지 지정 */ }
            <label for={item.id}>{item.title}</label>
        </ListItem>
    )
}

export default Todo;
