function App() {
  //우리는 하나의 할 일을 객체로 관리할 것이다.
  //id, title, done
  const [item, setItem] = useState([
  {
    id:"0",
    title:"Hello world 1",
    done: true
  },
  {
    id:"1",
    title:"Hello world 2",
    done: false
  }
])

for(let i = 0; i <= useState.length(); i++){
  return (
    <div className="App">
      <Todo item={item[i]}/>
    </div>
  );
}
}

export default App;
