import logo from './logo.svg';
import './App.css';
import { useState } from "react"
//경로는 현재 파일을 기준으로 목적지를 찾아가면 된다.
//import {Farewell, Greeting} from './Greetings'
import Todo from './Todo';
import AddTodo from './AddTodo';
import { Container, List, Paper } from '@mui/material';
import { Done } from '@mui/icons-material';

//Container
//레이아웃의 가로 폭을 제한하고, 중앙 정렬 및 가본 패딩을 자동으로 적용해주는 컴포넌트
//주요 props
//maxWidth : 최대 너비를 지정(xs, sm, md, lg, xl, false)
//fixed : maxWith

function App() {
  //우리는 하나의 할 일을 객체로 관리할 것이다.
  //id, title, done
  const [items, setItems] = useState([])

  //Todo를 직접 추가하기 위한 백엔드 콜을 대신할 가짜 함수를 만들어보자.
  const add = (item) => {
    //newItem 객체가 하나의 Todo
    const newItem = {
      ...item,
      id: "ID-"+ item.length,
      done:false,
    }

    setItems(prev => [...prev, newItem])
    console.log("items : ",[...items, newItem]);
  }

  //React는 key속성에 들어있는 값을 참고해서, 리스트의 요소가 변경될 경우 어떤 요소가 변경되었는지 빠르게 파악할 수 있다.
  const todoItems = items.length > 0 &&
    //Paper 컴포넌트 -> 종이같은 표면 효과를 제공하는 컨테이너 컴포넌트
    //elevation(그림자 깊이)를 통해 높낮이를 표현하고 배경색과 그림자 효과로 콘텐츠를 돋보이게 한다.
    <Paper style={{ margin: 16 }}>
      <List>
        {items.map((item) => (
          <Todo item={item} key={item.id} />
        ))}
      </List>
    </Paper>

  return (
    <div className="App">
      <Container maxWidth="md">
        <AddTodo add={add}/>{/* AddTodo에 add 함수를 전달. */}
        {todoItems}
      </Container>
    </div>
  );

}

export default App;
