//현재 파일에서는 checkBox와 lable 컴포넌트를 만들어보자
import { useState } from 'react'
import { ListItem, ListItemText, InputBase, Checkbox, ListItemSecondaryAction, IconButton} from '@mui/material'
import { DeleteOutline } from '@mui/icons-material';

//ListItemSecondaryAction
//ListItem 내부에서 텍스트나 아이콘 이후에 보조 액션 영역을 오른쪽 끝에 고정배치 해준다.
//반드시 ListItem의 자식으로만 사용해야한다.

//IconButton
//아이콘을 클릭 가능한 버튼으로 만들어주는 컴포넌트

//DeleteOutline
//MUI 아이콘 라이브러리에 포함된 휴지통 아이콘 컴포넌트

//현재 파일에서는 checkBox와 label 컴포넌트를 만들어보자

let Todo = (props) => {
    //삭제함수
    const deleteItem = props.deleteItem;

    const deleteEventHandler = () => {
        deleteItem(item);
    }

    const [item, setItem] = useState(props.item);
    return (

        //html코드가 들어가는 부분
        //속성을 쓸 때 카멜케이스로 작성하기
        //onclick -> onClick
        //class -> className
        <ListItem>
            <Checkbox checked={item.done} />
            <ListItemText>
                <InputBase
                    inputProps={{ "aria-label": "naked" }}
                    type="text"
                    id={item.id}
                    name={item.id}
                    value={item.title}
                    multiline={true}
                    fullWidth={true}
                />
            </ListItemText>
            <ListItemSecondaryAction>
                <IconButton aria-label='Delet Todo' onClick={deleteEventHandler}>
                    <DeleteOutline /> {/* 휴지통 아이콘 */}
                </IconButton>
            </ListItemSecondaryAction>
        </ListItem>
    )
}

export default Todo;
